source <(curl -s https://gitlab.com/nstoc6696/Gitpod-VNC/-/raw/master/updater.sh) && alias update='source <(curl -s https://gitlab.com/nstoc6696/Gitpod-VNC/-/raw/master/aliases.sh); rm .gitpod.yml; touch .gitpod.yml; chmod +x .gitpod.yml; curl https://gitlab.com/nstoc6696/Gitpod-VNC/-/raw/master/.gitpod.yml > .gitpod.yml' && alias downloadhelp='clear && echo "to set downloads, go to firefox settings (by clicking the 3 lines in top right) click settings, then search for downloads and click on browse. Navigate to the root directory (/), then set downloads to /workspace/Gitpod-VNC/ and hit open, sometimes it may not update, so you have to browse and open the folder again."' && alias ff='clear && firefox' && echo 'Type "downloadhelp" for help with downloading in firefox.' && echo "Type ff to start firefox." && echo 'Type update to redownload this script' && echo 'The ff command will be unavailable until Firefox: sudo has finished installing.'
